import React from 'react';

import axios from 'axios';
import './styles.css';

export default class App extends React.Component {
  state = {
    movies: [],
    showResults: false,
  }

  description() {
    // alert(movies.overview);
    showResults = true;
  }

  componentDidMount() {
    axios.get(`https://api.themoviedb.org/3/movie/upcoming?api_key=c1c06a942715cee34cef2401ebe90113`)
      .then(res => {
        const movies = res.data.results;
        this.setState({ movies });
        console.log(movies);

      })
  }

  render() {
    const Results = () => (
      <div id="results" className="search-results">
        Some Results
      </div>
    )
    return (
      <div>
      <h1 className="title">Tiff's movies of the year</h1>
      <ul>
        { this.state.movies.map( movies => <li onClick={this.description}>{movies.title} {movies.overview}</li>)}
      </ul>

      <input type="submit" value="Search" onClick={onClick} />
      { showResults ? <Results /> : null }
      </div>
    )



  }
}
