import React from 'react';

import axios from 'axios';
import './styles.css';

export default class App extends React.Component {
  state = {
    movies: [],
    showing: false,
    details: [],
    genre: "",
  }

  // description() {
  //   // alert(movies.overview);
  //   this.showing = true
  // }
  handleClick(postId) {
    event.currentTarget.classList.toggle('active');
    axios.get(`https://api.themoviedb.org/3/movie/${movie.id}?api_key=c1c06a942715cee34cef2401ebe90113&language=en-US`)
      .then(res => {
        const details = res.data;
        const genre = res.data.genres[0].name;
        this.setState({ details });
        this.setState({genre})
        console.log(details);
        this.showing = true;
      })
  }

  componentDidMount() {
    axios.get(`https://api.themoviedb.org/3/movie/upcoming?api_key=c1c06a942715cee34cef2401ebe90113`)
      .then(res => {
        const movies = res.data.results;

        this.setState({ movies });
        console.log(movies);

      })
  }

  render() {
    const { details } = this.state;
    const { genre } = this.state; 
    // const hi = showing ? details.runtime: 'hi';
    return (
      <div>
      <h1 className="title">Tiff's movies of the year</h1>
      {
        this.state.movies.map((movie, i) => {
          return (
            <div className="movie" onClick={this.handleClick.bind(this, movie.id)} key={i}>{movie.title}
              <div className="opening">Description: {movie.overview} Genre: {genre} Runtime: {details.runtime} min, tagline: {details.tagline}</div>
            </div>
          );
        })
      }

      </div>
    )


  }
}
