import React from 'react';

import axios from 'axios';
import './styles.css';

export default class App extends React.Component {
  state = {
    movies: [],
    showing: false,
    details: [],
  }

  // description() {
  //   // alert(movies.overview);
  //   this.showing = true
  // }
  handleClick = event => {
    event.currentTarget.classList.toggle('active');
    axios.get(`https://api.themoviedb.org/3/movie/${this.movie.id}?api_key=c1c06a942715cee34cef2401ebe90113&language=en-US`)
      .then(res => {
        const details = res.data;
        this.setState({ details });
        console.log(details);

      })
  }

  componentDidMount() {
    axios.get(`https://api.themoviedb.org/3/movie/upcoming?api_key=c1c06a942715cee34cef2401ebe90113`)
      .then(res => {
        const movies = res.data.results;
        const id = res.data
        this.setState({ movies });
        console.log(movies);

      })
  }

  render() {
    return (
      <div>
      <h1 className="title">Tiff's movies of the year</h1>
      {
        this.state.movies.map((movie, i) => {
          return (
            <div className="movie" onClick={this.handleClick} key={i}>{movie.title}
              <div className="opening">Description: {movie.overview} Genre: {movie.genres} {movie.id}</div>
            </div>
          );
        })
      }

      </div>
    )


  }
}
