import React from 'react';

import axios from 'axios';
import './styles.css';

export default class App extends React.Component {
  state = {
    movies: [],
    showing: false,
  }

  description() {
    // alert(movies.overview);
    this.showing = true
  }

  componentDidMount() {
    axios.get(`https://api.themoviedb.org/3/movie/upcoming?api_key=c1c06a942715cee34cef2401ebe90113`)
      .then(res => {
        const movies = res.data.results;
        this.setState({ movies });
        console.log(movies);

      })
  }

  render() {
    return (
      <div>
      <h1 className="title">Tiff's movies of the year</h1>
      <ul>
        { this.state.movies.map( movies => <li onClick={() => this.setState({ showing: !showing })}>{movies.title} {movies.overview}</li>)}
        { showing 
          ? <div>This is visible</div>
          : null
      }
      </ul>
      </div>
    )


  }
}
